import React from 'react'
import Recipe from "./Recipe"


export default function RecipeList({
    recipes,
    handleRecipeAdd,
    handleRecipeDelete,
}) {
    return (
        <>
            <div>
                {recipes.map(recipe => 
                    <Recipe 
                        key={recipe.id}
                        recipes={recipes}
                        handleRecipeDelete={handleRecipeDelete}

                    />
                )}
            </div>



            <button onClick={handleRecipeAdd}>Add recipe</button>
        </>

    )
}
